function setActiveLink(){let e=location.pathname.split("/").pop()||"index.html";document.querySelectorAll(".navbar .nav-link").forEach(t=>{let l=t.getAttribute("href");e===("./"===l?"index.html":l)?t.classList.add("active"):t.classList.remove("active")})}function enableSmoothAnchors(){document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",t=>{let l=e.getAttribute("href"),n=document.querySelector(l);n&&(t.preventDefault(),n.scrollIntoView({behavior:"smooth"}))})})}function enableScrollReveal(){let e=new IntersectionObserver((e,t)=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add("visible"),t.unobserve(e.target))})},{threshold:.12,rootMargin:"0px 0px -40px 0px"});document.querySelectorAll(".reveal").forEach(t=>e.observe(t))}function setupScrollTop(){let e=document.getElementById("scrollTopBtn");e&&(window.addEventListener("scroll",()=>{e.style.display=window.scrollY>300?"inline-flex":"none"}),e.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})}))}function collapseOnNavClick(){let e=document.querySelectorAll(".navbar .nav-link"),t=document.getElementById("navbarNav");e.forEach(e=>{e.addEventListener("click",()=>{if(t.classList.contains("show")){let e=bootstrap.Collapse.getInstance(t)||new bootstrap.Collapse(t,{toggle:!1});e.hide()}})})}document.addEventListener("DOMContentLoaded",()=>{setActiveLink(),enableSmoothAnchors(),enableScrollReveal(),setupScrollTop(),collapseOnNavClick()});